function ret = test2(n_hid)  n_params = (256+10) * n_hid;  as_row_vector = cos(0:(n_params-1));  n_hid_new = size(as_row_vector, 1) / (256+10);  fprintf('theta_to_model n_hid_new:%d\n',n_hid_new);  ret.input_to_hid = transpose(reshape(as_row_vector(1: 256*n_hid_new), 256, n_hid_new));  ret.hid_to_class = reshape(as_row_vector(256 * n_hid_new + 1 : size(as_row_vector,1)), n_hid_new, 10).';  endfunction